package com.amir.weathoraclass Kenos {    // Reverses a string using StringBuilder's reverse() method    fun reverseStringBuilder(input: String): String {        return StringBuilder(input).reverse().toString()    }    // Reverses a string using Kotlin's built-in reversed() function    fun reverseString(input: String): String {        return input.reversed()    }    // Reverses a string manually using a loop    fun reverseStringManual(input: String): String {        var result = ""        for (i in input.length - 1 downTo 0) {            result += input[i]        }        return result    }    // Finds the most frequent number in a list using grouping and eachCount()    fun getTheMostRepeated(list: List<Int>): Int {        return list.groupingBy { it }            .eachCount()            .maxByOrNull { it.value }!!            .key    }    // Finds the most frequent number manually using a HashMap    fun getTheMostRepeatedManual(list: List<Int>): Int {        val map = mutableMapOf<Int, Int>()        for (num in list) {            map[num] = map.getOrDefault(num, 0) + 1        }        return map.maxByOrNull { it.value }!!.key    }    // Checks if a number is aval    fun isAval(n: Int): Boolean {        if (n <= 1) return false        for (i in 2 until n) {            if (n % i == 0) return false        }        return true    }    // Calculates the sum of the main in square matrix    fun sumMainDiagonal(matrix: Array<IntArray>): Int {        var sum = 0        for (i in matrix.indices) {            sum += matrix[i][i]        }        return sum    }    // Sorts the words in a sentence alphabetically    fun sortWordsAlphabetly(sentence: String): String {        return sentence.split(" ")            .sorted()            .joinToString(" ")    }    // Sorts words in a sentence alphabetically with trimming and cleaning    fun sortWordsClean(sentence: String): String {        return sentence.trim()            .split("\\s+".toRegex())            .filter { it.isNotBlank() }            .sorted()            .joinToString(" ")    }}